package com.karn.leetcode.potd;

import java.util.concurrent.CompletableFuture;

public class ShortestPalindrome {
    public static void main(String[] args) {
        Thread.UncaughtExceptionHandler h = new Thread.UncaughtExceptionHandler() {
            @Override
            public void uncaughtException(Thread th, Throwable ex) {
                System.out.println("Uncaught exception: " + ex);
            }
        };
        Thread t = new Thread() {
            @Override
            public void run() {
                System.out.println("Sleeping ...");
                try {
                    Thread.sleep(1000);
                } catch (InterruptedException e) {
                    System.out.println("Interrupted.");
                }
                System.out.println("Throwing exception ...");
                throw new RuntimeException();
            }
        };
        t.setUncaughtExceptionHandler(h);
        t.start();
        long start = System.currentTimeMillis();
        System.out.println(new ShortestPalindrome().shortestPalindrome(""));
        System.out.printf("Finished in %s ms", (System.currentTimeMillis()-start));
    }
    public String shortestPalindrome(String s) {
        if(s.equals("")) return s;
        if(isPalindrome(s)) return s;
        int indexWithPalindromFront = getIndexForPalindrome(s);
        String subStringToAdd = new StringBuilder(s.substring(indexWithPalindromFront)).reverse().toString();
        return subStringToAdd + s;
    }
    private int getIndexForPalindrome(String s){
        int index = 1;
        for(int i=s.length()-1;i>=0;i--){
            if(isPalindrome(s.substring(0,i))){
                index = i;
                return index;
            }
        }
        return index;
    }
    private boolean isPalindrome(String s){
        int length = s.length();
        if(length==1) return true;
        if(length%2==0){
            int i= length/2-1;
            int j= i+1;
            while(i>=0&&j<length){
                if(s.charAt(i--)!=s.charAt(j++)){
                    return false;
                }
            }
            return true;
        }else{
            int i= length/2;
            int j= i;
            while(i>=0&&j<length){
                if(s.charAt(i--)!=s.charAt(j++)){
                    return false;
                }
            }
            return true;
        }
    }
}
